import React, { useState ,useRef, useEffect} from 'react';
import './App.css';


const Dot = React.forwardRef(({ onDotClick, index }, ref) => (
  <div ref={ref} className="dot" onMouseDown={() => onDotClick(index)}></div>
));



const App = () => {
 const [topVertices, setTopVertices] = useState(3);
 const [bottomVertices, setBottomVertices] = useState(3);
 const [darkMode, setDarkMode] = useState(false);
 const [lines, setLines] = useState([]); 
 const [drawing, setDrawing] = useState(false);
 const MAX_VERTICES = 50;
 const dotRefs = useRef([]);
 const MIN_VERTICES = 3;
 const svgRef = useRef(null);
 const colors = ['purple', 'lightblue', 'green', 'red', 'orange', 'pink', 'mediumslateblue', 'mediumseagreen', 'rgb(183, 183, 244)', 'rosybrown', 'olivedrab', 'crimson', 'rgb(213, 213, 55)', 'palevioletred', 'indigo', 'coral', 'teal', 'plum', 'navy', 'yellowgreen'];
 const Dot = React.forwardRef(({ onDotClick }, ref) => (
   <div ref={ref} className="dot" onMouseDown={() => onDotClick(ref)}></div>
 ));




 const [currentLine, setCurrentLine] = useState(null);
 const [colorIndex, setColorIndex] = useState(0);




 const getDotPosition = (index) => {
  const dotRef = dotRefs.current[index];
  if (!svgRef.current || !dotRef) {
    return { x: 0, y: 0 };
  }
  const svgRect = svgRef.current.getBoundingClientRect();
  const dotRect = dotRef.getBoundingClientRect();
  return {
    x: dotRect.left + dotRect.width / 2 - svgRect.left,
    y: dotRect.top + dotRect.height / 2 - svgRect.top,
  };
};






 const handleMouseDown = (dotRef) => {
   if (drawing) return;
    const start = getDotPosition(dotRef);
   setDrawing(true);
   setLines([...lines, { start, end: null }]);
 };
 const handleMouseMove = (e) => {
  if (!drawing || currentLine === null) return;

  const rect = svgRef.current.getBoundingClientRect();
  const newEnd = {
    x: e.clientX - rect.left,
    y: e.clientY - rect.top,
  };

  setCurrentLine((prevLine) => ({
    ...prevLine,
    x2: newEnd.x,
    y2: newEnd.y,
  }));
};


  const handleMouseUp = () => {
   setDrawing(false);
 };
  const undoLastLine = () => {
   setLines(lines.slice(0, -1));
 };








 const startLine = (event) => {
   if (drawing) return;
   setDrawing(true);
   const rect = svgRef.current.getBoundingClientRect();
   const newLine = {
     x1: event.clientX - rect.left,
     y1: event.clientY - rect.top,
     x2: event.clientX - rect.left,
     y2: event.clientY - rect.top,
     color: colors[colorIndex]
   };
   setCurrentLine(newLine);
 };


 const createLine = (event) => {
   if (!drawing) return;
   const rect = svgRef.current.getBoundingClientRect();
   setCurrentLine(prevLine => ({
     ...prevLine,
     x2: event.clientX - rect.left,
     y2: event.clientY - rect.top
   }));
 };


 const endLine = () => {
   if (!drawing) return;
   setLines(prevLines => [...prevLines, currentLine]);
   setCurrentLine(null);
   setDrawing(false);
   setColorIndex((prevIndex) => (prevIndex + 1) % colors.length);
 };


 useEffect(() => {
   dotRefs.current = dotRefs.current.slice(0, topVertices + bottomVertices);
 }, [topVertices, bottomVertices]);

const distance = (x1, y1, x2, y2) => {
  return Math.sqrt(Math.pow(x2 - x1, 2) + Math.pow(y2 - y1, 2));
};
const closestPointOnLine = (lx1, ly1, lx2, ly2, x0, y0) => {
  const lineLength = distance(lx1, ly1, lx2, ly2);
  if (lineLength === 0) return { x: lx1, y: ly1 };

  let t = ((x0 - lx1) * (lx2 - lx1) + (y0 - ly1) * (ly2 - ly1)) / Math.pow(lineLength, 2);
  t = Math.max(0, Math.min(1, t));
  return {
    x: lx1 + t * (lx2 - lx1),
    y: ly1 + t * (ly2 - ly1),
  };
};
const isPointNearLine = (lx1, ly1, lx2, ly2, x0, y0, tolerance) => {
  const closestPoint = closestPointOnLine(lx1, ly1, lx2, ly2, x0, y0);
  const d = distance(x0, y0, closestPoint.x, closestPoint.y);
  return d <= tolerance;
};
const shouldAttachToNewVertex = (line, newVertexIndex, tolerance = 10) => {
  const startPos = getDotPosition(line.start);
  const endPos = getDotPosition(line.end);
  const newVertexPos = getDotPosition(newVertexIndex);
  return isPointNearLine(startPos.x, startPos.y, endPos.x, endPos.y, newVertexPos.x, newVertexPos.y, tolerance);
};






const handleDotClick = (index) => {
  const position = getDotPosition(index);
  if (currentLine) {
    setCurrentLine({ ...currentLine, x2: position.x, y2: position.y, color: colors[colorIndex] });
    setLines([...lines, currentLine]);
    setCurrentLine(null);
    setColorIndex((prevIndex) => (prevIndex + 1) % colors.length);
  } else {
    setCurrentLine({ x1: position.x, y1: position.y, x2: position.x, y2: position.y, color: colors[colorIndex] });
  }
  setDrawing(!drawing);
};



const renderDots = (count, offset) => {
  return Array.from({ length: count }, (_, index) => (
    <Dot
      key={index}
      index={offset + index}
      ref={el => dotRefs.current[offset + index] = el}
      onDotClick={handleDotClick}
    />
  ));
};




 const toggleDarkMode = () => {
   setDarkMode(!darkMode);
 };


 return (
   <div className={`App ${darkMode ? 'dark-mode' : ''}`}>
     <header>
       <link rel="preconnect" href="https://fonts.googleapis.com" />
       <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin />
       <link href="https://fonts.googleapis.com/css2?family=DotGothic16&display=swap" rel="stylesheet" />
     </header>
     <div className="title">
       <h1>ColorTaiko!</h1>
     </div>
     <div className="container" onMouseMove={handleMouseMove} onMouseUp={handleMouseUp}>
       <div className="input-container">
         <label htmlFor="top-vertices">Top Vertices:</label>
         <input
           id="top-vertices"
           value={topVertices}
           min={MIN_VERTICES}
           max={MAX_VERTICES}
           maxLength={2}
           onChange={(e) => {
             const newValue = Math.min(Math.max(parseInt(e.target.value), MIN_VERTICES), MAX_VERTICES);
             setTopVertices(newValue);
           }}
         />
         <button onClick={() => setTopVertices(Math.min(topVertices + 1, MAX_VERTICES))}>+</button>
         <button onClick={() => setTopVertices(Math.max(topVertices - 1, MIN_VERTICES))}>-</button>
       </div>
       <div className="input-container">
         <label htmlFor="bottom-vertices">Bottom Vertices:</label>
         <input
           id="bottom-vertices"
           type="number"
           value={bottomVertices}
           min={MIN_VERTICES}
           max={MAX_VERTICES}
           maxLength={2}
           onChange={(e) => {
             const newValue = Math.min(Math.max(parseInt(e.target.value), MIN_VERTICES), MAX_VERTICES);
             setBottomVertices(newValue);
           }}
         />
         <button onClick={() => setBottomVertices(Math.min(bottomVertices + 1, MAX_VERTICES))}>+</button>
         <button onClick={() => setBottomVertices(Math.max(bottomVertices - 1, MIN_VERTICES))}>-</button>
       </div>
       <div className="vertices-container">
         {renderDots(topVertices, 0)}
       </div>
       <div className="vertices-container" style={{ marginTop: '100px' }}>
         {renderDots(bottomVertices, topVertices)}
       </div>
       <svg ref={svgRef} onMouseMove={handleMouseMove} onMouseUp={handleMouseUp} style={{ width: '100%', height: '100%' }}>
      {lines.map((line, index) => (
        <line key={index} x1={line.x1} y1={line.y1} x2={line.x2} y2={line.y2} stroke={line.color} strokeWidth="2" />
      ))}
      {currentLine && (
        <line
          x1={currentLine.x1}
          y1={currentLine.y1}
          x2={currentLine.x2}
          y2={currentLine.y2}
          stroke={currentLine.color}
          strokeWidth="2"
        />
      )}
    </svg>


     </div>
     <button id = 'undoButton' onClick={undoLastLine}>Undo Last Line</button>
     <button id="modeSwitch" onClick={toggleDarkMode}>{darkMode ? 'Light Mode' : 'Dark Mode'}</button>
   </div>
 );
}


export default App;
