import React, { useState, useRef, useEffect } from 'react';
import './App.css';

const App = () => {
  const [topVertices, setTopVertices] = useState(3);
  const [bottomVertices, setBottomVertices] = useState(3);
  const [darkMode, setDarkMode] = useState(false);
  const [selectedDots, setSelectedDots] = useState([]);
  const [lines, setLines] = useState([]);
  const [dotPositions, setDotPositions] = useState([]);

  const topContainerRef = useRef(null);
  const bottomContainerRef = useRef(null);

  const MAX_VERTICES = 50;
  const MIN_VERTICES = 3;

  const renderDots = (count, offset) => {
    return Array.from({ length: count }, (_, index) => {
      const position = getDotPosition(index, count);
      return (
        <button
          key={offset + index}
          className='dot'
          onClick={() => handleDotClick(offset + index, position)}
          style={{ position: 'flex', left: `${position.x}px`, top: `${position.y}px` }}
        ></button>
      );
    });
  };
  const updateDotPositions = () => {
    const topPositions = Array.from(topContainerRef.current.children).map(dot => dot.getBoundingClientRect());
    const bottomPositions = Array.from(bottomContainerRef.current.children).map(dot => dot.getBoundingClientRect());
    setDotPositions(topPositions.concat(bottomPositions));
  };


  useEffect(() => {
    updateDotPositions();
    // Add a resize listener to update positions when the window is resized
    window.addEventListener('resize', updateDotPositions);
    return () => window.removeEventListener('resize', updateDotPositions);
  }, [topVertices, bottomVertices]);




  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  const containerRef = useRef(null);
  const getDotPosition = (index, count) => {
    const containerRect = containerRef.current.getBoundingClientRect();
    const spacing = containerRect.width / count;
    return {
      x: containerRect.left + spacing / 2 + spacing * index,
      y: containerRect.top + containerRect.height / 2 // Assuming a single row
    };
  };


  const handleDotClick = (index) => {
    const newSelectedDots = [...selectedDots, index];
    if (newSelectedDots.length === 2) {
      const newLines = [...lines, { from: dotPositions[newSelectedDots[0]], to: dotPositions[newSelectedDots[1]] }];
      setLines(newLines);
      setSelectedDots([]);
    } else {
      setSelectedDots(newSelectedDots);
    }
  };

  return (
    <div className={`App ${darkMode ? 'dark-mode' : ''}`}>
      <header>
        <link rel="preconnect" href="https://fonts.googleapis.com" />
        <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin />
        <link href="https://fonts.googleapis.com/css2?family=DotGothic16&display=swap" rel="stylesheet" />
      </header>
      <div className="title">
        <h1>ColorTaiko!</h1>
      </div>

      <div className="container" ref={containerRef}>
        {/* ...input containers... */}
        <div className="vertices-container">
          {renderDots(topVertices, 0)}
        </div>
        <div className="vertices-container">
          {renderDots(bottomVertices, MAX_VERTICES)}
        </div>
      </div>
      <div className="container">
        <div className="input-container">
          <label htmlFor="top-vertices">Top Vertices:</label>
          <input
            id="top-vertices"
            value={topVertices}
            min={MIN_VERTICES}
            max={MAX_VERTICES}
            maxLength={2}
            onChange={(e) => {
              const newValue = Math.min(Math.max(parseInt(e.target.value), MIN_VERTICES), MAX_VERTICES);
              setTopVertices(newValue);
            }}
          />
          <button onClick={() => setTopVertices(Math.min(topVertices + 1, MAX_VERTICES))}>+</button>
          <button onClick={() => setTopVertices(Math.max(topVertices - 1, MIN_VERTICES))}>-</button>
        </div>
        <div className="input-container">
          <label htmlFor="bottom-vertices">Bottom Vertices:</label>
          <input
            id="bottom-vertices"
            type="number"
            value={bottomVertices}
            min={MIN_VERTICES}
            max={MAX_VERTICES}
            maxLength={2}
            onChange={(e) => {
              const newValue = Math.min(Math.max(parseInt(e.target.value), MIN_VERTICES), MAX_VERTICES);
              setBottomVertices(newValue);
            }}
          />
          <button onClick={() => setBottomVertices(Math.min(bottomVertices + 1, MAX_VERTICES))}>+</button>
          <button onClick={() => setBottomVertices(Math.max(bottomVertices - 1, MIN_VERTICES))}>-</button>
        </div>
        <div className="vertices-container">
          {renderDots(topVertices)}
        </div>
        <div className="vertices-container">
          {renderDots(bottomVertices)}
        </div>
      </div>
      <button id="modeSwitch" onClick={toggleDarkMode}>{darkMode ? 'Light Mode' : 'Dark Mode'}</button>
    </div>
  );
}

export default App;
