import React from 'react';
import { EdgeText, getMarkerEnd } from 'react-flow-renderer';

const LargeArcEdge = ({
  sourceX,
  sourceY,
  targetX,
  targetY,
  style = {},
  data,
}) => {
  // Calculate the midpoint between the source and target nodes
  const mx = (sourceX + targetX) / 2; // Midpoint x-coordinate

  // Calculate the control point coordinates based on the midpoint
  const cx = mx; // Control point x-coordinate (same as midpoint)
  const cy = sourceY; // Control point y-coordinate (adjust as needed)

  // Define the path for the curve using the quadratic Bezier curve formula
  const path = `M${sourceX},${sourceY} Q${cx},${cy} ${targetX},${targetY}`;

  // Get the marker end for the edge
  const markerEnd = getMarkerEnd(data?.arrowHeadType, 'edge');

  return (
    <>
      <path
        className="animated"
        d={path}
        style={{
          stroke: style.stroke,
          strokeWidth: style.strokeWidth,
          fill: 'none',
          ...style.additionalEdgeStyles,
        }}
        markerEnd={markerEnd}
      />
      {data?.label && (
        <EdgeText x={data.labelX} y={data.labelY} style={style.labelStyle || {}}>
          {data.label}
        </EdgeText>
      )}
    </>
  );
};

export default LargeArcEdge;
