import React from 'react';
import { getBezierPath, getMarkerEnd } from 'react-flow-renderer';

const LargeArcEdge = ({
  id,
  sourceX,
  sourceY,
  targetX,
  targetY,
  sourcePosition,
  targetPosition,
  style = {},
  arrowHeadType,
  markerEndId,
}) => {
  const midX = (sourceX + targetX) / 2;
  const midY = (sourceY + targetY) / 2;
  const cp1Y = sourceY - 50; 
  const cp2Y = targetY - 50; 

  // Original edge path
  const edgePath = `M${sourceX},${sourceY} C${(sourceX + midX) / 2},${cp1Y} ${(targetX + midX) / 2},${cp2Y} ${targetX},${targetY}`;

  // Middle marker
  const midMarkerPath = `M${midX},${midY} l0,0`;
  const markerEnd = getMarkerEnd(arrowHeadType, markerEndId);


  return (
    <>
      <path
        style={style}
        className="react-flow__edge-path"
        d={edgePath}
        markerEnd={markerEnd}
      />
      {/* Additional path for the middle arrow */}
      <path
        d={midMarkerPath}
        style={style}
        className="react-flow__edge-path"
        markerEnd={markerEnd}
      />
    </>
  );
};

export default LargeArcEdge;
