import React from 'react';
import { getBezierPath, getMarkerEnd } from 'react-flow-renderer';
import { EdgeText } from 'react-flow-renderer';
import { useDispatch } from 'react-redux';
import { FONT_FAMILY } from '../constant';
import { REMOVE_EDGE, REMOVE_ELEMENT } from '../reducer/const';
const GetTrashSvg = require('./assets/delete-trash-icon.svg');

const LargeArcEdge = ({
  id,
  sourceX,
  sourceY,
  targetX,
  targetY,
  sourcePosition,
  targetPosition,
  style = {},
  arrowHeadType,
  markerEndId,
  data, // Added for the data object
}) => {
  // Implement the custom large arc drawing logic here, setting the color based on data.color
  // Calculate the control point coordinates for the arc
  const controlX = (sourceX + targetX) / 2;
  const controlY = sourceY - Math.abs(targetY - sourceY) * 0.5;

  // Define the path for the arc
  const edgePath = `M${sourceX},${sourceY} Q${controlX},${controlY} ${targetX},${targetY}`;

  // Get the marker end for the edge
  const markerEnd = getMarkerEnd(arrowHeadType, markerEndId);

  const dispatch = useDispatch();

  return (
    <>
      <path
        id={id}
        className="react-flow__edge-path"
        d={edgePath}
        markerEnd={markerEnd}
        style={{
          ...style,
          stroke: data?.color || 'black', // Use the color specified in the data object, or default to black if not specified
        }}
      />
      <EdgeText
        x={(sourceX + targetX) / 2} // Placing the label at the midpoint of the edge
        y={sourceY} // Adjust as needed
        label={data?.label} // Added for the data object
        labelStyle={{
          fill: data?.labelColor || '#222222', // Use the label color specified in the data object, or default to black if not specified
          fontFamily: FONT_FAMILY,
          fontSize: '14px',
        }}
        labelShowBg
        labelBgStyle={{
          fill: data?.labelBgColor || 'white', // Use the label background color specified in the data object, or default to white if not specified
          stroke: data?.labelBorderColor || '#e9e9e9', // Use the label border color specified in the data object, or default to a light grey if not specified
        }}
        labelBgPadding={[6, 3]}
        labelBgBorderRadius={4.8}
      />
      <svg style={{ position: 'absolute', left: 0, top: 0, zIndex: 9999 }}>
        <g
          id="trashIcon"
          style={{
            transform: `translate(${(sourceX + targetX) / 2 - 14.5}px, ${sourceY - 14.5}px)`, // Placing the trash icon at the midpoint of the edge
          }}
        >
          <GetTrashSvg
            style={{
              cursor: 'pointer',
              fill: '#344cfa',
            }}
            onClick={() => dispatch({ type: REMOVE_EDGE, payload: [id] })}
          />
        </g>
      </svg>
    </>
  );
};

export default LargeArcEdge;
