import React from 'react';
import { getBezierPath, getMarkerEnd } from 'react-flow-renderer';

const CustomCurvedEdge = ({
  id,
  sourceX,
  sourceY,
  targetX,
  targetY,
  sourcePosition,
  targetPosition,
  style = {},
  arrowHeadType,
  markerEndId,
}) => {
  // Determine if the edge is horizontal or vertical
  const isHorizontal = sourceY === targetY;

  // Calculate control points based on the edge orientation
  const controlPointX = isHorizontal ? targetX : sourceX + (targetX - sourceX) / 2;
  const controlPointY = isHorizontal ? sourceY + (targetY - sourceY) / 2 : targetY;

  // Construct the custom curve path
  const edgePath = `M${sourceX},${sourceY} C${controlPointX},${sourceY} ${controlPointX},${targetY} ${targetX},${targetY}`;
  const markerEnd = getMarkerEnd(arrowHeadType, markerEndId);

  // Merge default style with custom style
  const edgeStyle = {
    ...style,
    strokeWidth: 2, // Example: Custom stroke width
    stroke: 'blue', // Example: Custom stroke color
  };

  return (
    <path
      style={edgeStyle}
      className="react-flow__edge-path"
      d={edgePath}
      markerEnd={markerEnd}
    />
  );
};

export default CustomCurvedEdge;
