import React from 'react';
import { EdgeText, getMarkerEnd, getSmoothStepPath } from 'react-flow-renderer';

const LargeArcEdge = ({
  sourceX,
  sourceY,
  targetX,
  targetY,
  sourcePosition,
  targetPosition,
  style = {},
  data,
}) => {
  const path = getSmoothStepPath({
    sourceX,
    sourceY,
    targetX,
    targetY,
    sourcePosition,
    targetPosition,
    borderRadius: 16, // You can adjust the borderRadius to control the curvature
  });

  const markerEnd = data.arrowHeadType ? getMarkerEnd(data.arrowHeadType, 'edge') : null;

  return (
    <>
      <path
        className="animated"
        d={path}
        style={{
          stroke: style.stroke,
          strokeWidth: style.strokeWidth,
          fill: 'none',
          ...style.additionalEdgeStyles,
        }}
        markerEnd={markerEnd}
      />
      {data.label && (
        <EdgeText x={data.labelX} y={data.labelY} style={style.labelStyle || {}}>
          {data.label}
        </EdgeText>
      )}
    </>
  );
};

export default LargeArcEdge;
